// heal
vars: address otherUsr, uint256 daiSenderBefore, uint256 sinSenderBefore, uint256 viceBefore, uint256 debtBefore, uint256 daiOtherBefore, uint256 sinOtherBefore, uint256 daiSenderAfter, uint256 sinSenderAfter, uint256 viceAfter, uint256 debtAfter, uint256 daiOtherAfter, uint256 sinOtherAfter
spec: finished(Vat.heal(rad), (otherUsr != sender) |=> (((daiSenderBefore = old(dai(sender))) && (sinSenderBefore = old(sin(sender))) && (viceBefore = old(vice())) && (debtBefore = old(debt())) && (daiOtherBefore = old(dai(otherUsr))) && (sinOtherBefore = old(sin(otherUsr))) && (daiSenderAfter = (dai(sender))) && (sinSenderAfter = (sin(sender))) && (viceAfter = (vice())) && (debtAfter = (debt())) && (daiOtherAfter = (dai(otherUsr))) && (sinOtherAfter = (sin(otherUsr)))) ==> ((daiSenderAfter = daiSenderBefore - rad) && (sinSenderAfter = sinSenderBefore - rad) && (viceAfter = viceBefore - rad) && (debtAfter = debtBefore - rad) && (daiOtherAfter = daiOtherBefore) && (sinOtherAfter = sinOtherBefore))))